DROP TABLE IF EXISTS `BS`;
CREATE TABLE `BS` (`networkID` varchar(20),`basestationID` varchar(45) DEFAULT '',`signalStrength` int DEFAULT 0,`frequency` float DEFAULT 0,`networkType` varchar(20) DEFAULT 0,`maxBitRate` int DEFAULT 0,`guaranteedBitRate` int DEFAULT 0,`netLoad` int DEFAULT 0,`provider` varchar(45) DEFAULT '',`range` float DEFAULT 0,`x` float DEFAULT 0,`y` float DEFAULT 0,`port` int DEFAULT 0,`chargeModel` varchar(20) DEFAULT '',PRIMARY KEY (`networkID`),UNIQUE KEY `networkID_UNIQUE` (`networkID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;